                    Array.Reverse(file);

                    var s = new RawSourceWaveStream(new MemoryStream(file), new WaveFormat(44100, 16, 2));
                    int blockAlign = s.WaveFormat.BlockAlign;

                    byte[] buffer = new byte[blockAlign];
                    long samples = s.Length / blockAlign;
                    for (long sample = samples - 1; sample >= 0; sample--)
                    {
                        s.Position = sample * blockAlign;
                        s.Read(buffer, 0, blockAlign);
                        //writer.WriteData(buffer, 0, blockAlign);
                        outputFile.Write(buffer, 0, blockAlign);
                    }

                    int songEndAddr = (int)s.Length + 2048;
                    string hexVal = songEndAddr.ToString("x8");
                    byte[] hexValb = StringToByteArray(hexVal);
                    outputFile.Position = 20;
                    outputFile.Write(hexValb, 0, 4);

                    byte[] hexLengthSong = StringToByteArray(s.Length.ToString("x8"));
                    outputFile.Position = 67;
                    outputFile.Write(hexLengthSong, 0, 4);
                    outputFile.Position = 84;
                    Array.Reverse(hexLengthSong);
                    outputFile.Write(hexLengthSong, 0, 4);
